import React, {useEffect, useState} from 'react'

export default function TodoList(props){
    const [todos, setTodos] = useState([]);
    
    const [inputText, setInputText] = useState([{data: 'teste'}]);
    

    useEffect(()=>{
        let stTodos = localStorage.getItem('todos', JSON.parse(localStorage.getItem('todos')));
        if (stTodos){
            setTodos(stTodos);
        }
    },[])
    useEffect(()=>{
        // localStorage.setItem('')
        console.log('atualizou o todolist:D')
        localStorage.setItem('todos',JSON.stringify(todos));
    },[todos])
    function handleInputText(e){
        let text = e.target.value;
        setInputText(text);
        console.log(inputText);
    }

    function handleClick(e){
        if(todos.find(todo=> todo.data == inputText)){
            return
        }
        let newTodos = [...todos,
            {
                data: inputText
            }
        ];
        setTodos(newTodos);


    }

    return(
        <>
            <input onChange={handleInputText} type="text"></input>
            <button onClick={handleClick}>Adicionar todo-list</button>
            {todos.map((i)=>{
               return <h1 key={i.data}> {i.data}</h1>
            })}
            
        </>
    )
}
